export async function seedRegularTests() {
  console.log('Seeding regular tests...\n');

  for (const [key, testData] of Object.entries(REGULAR_TEST_DATA)) {
    const now = new Date();

    // Check if test already exists
    const existingTest = await prisma.test.findFirst({
      where: { title: testData.title }
    });

    if (existingTest) {
      console.log(`⏭️  Test already exists: ${testData.title}`);
      continue;
    }

    // Create test
    const test = await prisma.test.create({
      data: {
        title: testData.title,
        description: testData.description,
        subject: testData.subject,
        examType: testData.examType,
        targetGrade: testData.targetGrade,
        isDiagnostic: false,
        randomizeQuestions: true,
        preventBackNavigation: false,
        timeLimit: testData.timeLimit,
        passingScore: testData.passingScore,
        createdAt: now,
        updatedAt: now,
      },
    });

    // Create questions and link to test
    for (let i = 0; i < testData.questions.length; i++) {
      const q = testData.questions[i];
      const qNow = new Date();

      const question = await prisma.question.create({
        data: {
          subject: testData.subject,
          examType: testData.examType,
          targetGrade: testData.targetGrade,
          type: q.type,
          difficulty: q.difficulty,
          question: q.question,
          options: JSON.stringify(q.options),
          correctAnswer: JSON.stringify(q.correctAnswer),
          topic: q.topic,
          createdAt: qNow,
          updatedAt: qNow,
        },
      });

      await prisma.testQuestion.create({
        data: {
          testId: test.id,
          questionId: question.id,
          order: i,
        },
      });
    }

    console.log(`✅ Created test: ${testData.title}`);
  }

  console.log('\n✨ Regular tests seeded successfully!');
}

// Запуск, если файл запущен напрямую
if (require.main === module) {
  seedRegularTests()
    .catch((e) => {
      console.error(e);
      process.exit(1);
    })
    .finally(async () => {
      await prisma.$disconnect();
    });
}
